sys_script_client.script:
function onChange(control, oldValue, newValue, isLoading, isTemplate) {
   if (isLoading || newValue === '') {
	   if(g_form.isNewRecord()){
		 g_form .clearOptions('u_what_needed');  
	   }	   
      return;
   }	

	  if(newValue=='hr'){
		   g_form .clearOptions('u_what_needed');  
		  g_form.addOption('u_what_needed', 'hr1', 'Human resource 1 ');
		   g_form.addOption('u_what_needed', 'hr2', 'Human resource 2');
	  }
	   if(newValue=='facilities'){
		   g_form .clearOptions('u_what_needed');  
		  g_form.addOption('u_what_needed', 'facilities1', 'Facility 1 ');
		   g_form.addOption('u_what_needed', 'facilities2', 'Facility 2');
	  }

 if(newValue=='legal'){
		   g_form .clearOptions('u_what_needed');  
		  g_form.addOption('u_what_needed', 'legal1', 'Legal 1 ');
		   g_form.addOption('u_what_needed', 'legal2', 'Legal 2');
	  }
	 if(newValue=='your_choice'){
		   g_form .clearOptions('u_what_needed');  
		  g_form.addOption('u_what_needed', 'your_choice1', 'Department 1 ');
		   g_form.addOption('u_what_needed', 'your_choice2', 'Department 2');
	  }
}
